import { Component } from '@angular/core';
import { ResourceService } from '../resource.service';
import { Router } from '@angular/router';
import { House } from '../house';
import { Location } from '@angular/common';

@Component({
  selector: 'houses',
  templateUrl: './houses.component.html',
  styleUrls: ['./houses.component.css']
})
export class HousesComponent {

  constructor(private resourceService: ResourceService, private router: Router, private location: Location) {}

  ngOnInit(): void {
    this.getHouses()
  }

  houses: Set<House> = new Set();

  getHouses() {
    for (let i = 1; i < 10; i++) {
      this.resourceService.getHouses(i).subscribe(houses => {
        houses.forEach(house => this.houses.add(house))
      });
    }
  }

  getHouseId(house: House): string {
    return house.url.split("/").at(-1) ?? ""
  }

  getName(id: string): string {
    for (const house of this.houses) {
      if(house.url == id) {
        if(house.name != "") {
          return house.name
        }
        else if(house.titles.length > 0 && house.titles[0] != "") {
          return house.titles[0]
        }
        return "Unknown (" + (id.split("/").at(-1) ?? -1) + ")"
      }
    }
    return "Unknown (" + (id.split("/").at(-1) ?? -1) + ")"
  }

  goBack(): void {
    this.location.back();
  }
}
