import { Component } from '@angular/core';
import { Location } from '@angular/common';
import { Character } from 'src/app/character';
import { ActivatedRoute } from '@angular/router';
import { ResourceService } from 'src/app/resource.service';

@Component({
  selector: 'app-characters-detail',
  templateUrl: './characters-detail.component.html',
  styleUrls: ['./characters-detail.component.css']
})
export class CharactersDetailComponent {

  constructor(
    private route: ActivatedRoute,
    private resourceService: ResourceService,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.getCharacter();
    this.getCharacters();
  }

  character: Character = {
    url: "",
    name: "",
    gender: "",
    culture: "",
    born: "",
    died: "",
    titles: [],
    aliases: [],
    father: "",
    mother: "",
    spouse: "",
    allegiances: [],
    books: [],
    povBooks: [],
    tvSeries: [],
    playedBy: []
  }

  characters: Map<number, string> = new Map()

  getCharacter(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.resourceService.getCharacter(id)
      .subscribe(character => this.character = character);
  }

  getCharacters(): void {
    for (let i = 1; i < 44; i++) {
      this.resourceService.getCharacters(i).subscribe(characters => {
        characters.forEach(character => {
          const id = Number(character.url.split("/").at(-1) ?? -1)
          this.characters.set(id, character.name)
        })
      });
    }
  }

  getName(character: string): string {
    const id = Number(character.split("/").at(-1) ?? -1)
    if(id >= 0) {
      const name = this.characters.get(id)
      if(name == "") {
        return "Unknown (" + id + ")"
      }
      else {
        return this.characters.get(id) ?? "Name not found (" + id + ")"
      }
    }
    else {
      return "Character not found"
    }
  }

  goBack(): void {
    this.location.back();
  }
}
