import { Component } from '@angular/core';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
import { Book } from 'src/app/book';
import { ResourceService } from 'src/app/resource.service';
import { Character } from 'src/app/character';

@Component({
  selector: 'app-books-detail',
  templateUrl: './books-detail.component.html',
  styleUrls: ['./books-detail.component.css']
})
export class BooksDetailComponent {

  constructor(
    private route: ActivatedRoute,
    private resourceService: ResourceService,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.getBook();
    this.getCharacters();
  }

  book: Book = {
    url: "",
    name: "",
    isbn: "",
    authors: [],
    numberOfPages: 0,
    publisher: "",
    country: "",
    mediaType: "",
    released: "",
    characters: [],
    povCharacters: []
  }

  characters: Map<number, string> = new Map()

  getBook(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.resourceService.getBook(id)
      .subscribe(book => this.book = book);
  }

  getCharacters(): void {
    for (let i = 1; i < 44; i++) {
      this.resourceService.getCharacters(i).subscribe(characters => {
        characters.forEach(character => {
          const id = Number(character.url.split("/").at(-1) ?? -1)
          this.characters.set(id, character.name)
        })
      });
    }
  }

  getCharacterId(character: string): string {
    return "characters/"+character.split("/").at(-1) ?? ""
  }

  getDate(date: string): string {
    return date.split("T")[0].split("-").reverse().join("/")
  }

  getName(character: string): string {
    const id = Number(character.split("/").at(-1) ?? -1)
    return this.characters.get(id) ?? "Name not found ("+id+")"
  }

  goBack(): void {
    this.location.back();
  }
}
